import React, { useState } from 'react'

const Product = () => {
    const [products, setProducts] = useState([
        {"id":1, "name":"냉장고", "price":100},
        {"id":2, "name":"세탁기", "price":80},
        {"id":3, "name":"건조기", "price":80}
    ]);
    const [form, setForm] = useState({
        id:4,
        name:'',
        price:0
    });
    const {id, name, price} = form;
    const onInsert = (e) => {
        e.preventDefault();
    }
    return (
        <div>
            <h1>상품관리</h1>
            <div>
                <form onSubmit={onInsert}>
                    <h3>아이디 : {id}</h3>
                    <input placeholder='상품명' value={name}/>&nbsp;&nbsp;
                    <input placeholder='가격' value={price}/>&nbsp;&nbsp;
                    <button>등록</button>
                </form>
            </div>
            <hr/>
            <table>
                {products.map(p=>
                    <tr key={p.id}>
                        <td>{p.id}</td>
                        <td>{p.name}</td>
                        <td>{p.price}</td>
                    </tr>
                )}
            </table>
        </div>
    )
}

export default Product
