import React, { useState } from 'react'

const Product = () => {
    const [products, setProducts] = useState([
        {"id":1, "name":"냉장고", "price":100},
        {"id":2, "name":"세탁기", "price":80},
        {"id":3, "name":"건조기", "price":80}
    ]);
    return (
        <div>
            <h1>상품관리</h1>
            <table>
                {products.map(p=>
                    <tr key={p.id}>
                        <td>{p.id}</td>
                        <td>{p.name}</td>
                        <td>{p.price}</td>
                    </tr>
                )}
            </table>
        </div>
    )
}

export default Product
