import React from 'react'
import '../../App.css'

const Count = () => {
    const buttonStyle={
        background:'purple',
        fontSize:30,
        color:'white',
        cursor:'pointer'
    }
    return (
        <div className='count'>
            <button style={buttonStyle}>감소</button>
            <span style={{fontSize:30}}>0</span>
            <button style={buttonStyle}>증가</button>
        </div>
    )
}

export default Count
