import React from 'react'

const Count = () => {
    const buttonStyle={
        background:'cyan',
        fontSize:30,
        color:'white'
    }
    return (
        <div className='count' style={{marginLeft:'100px'}}>
            <button style={buttonStyle}>감소</button>
            <span style={{marginLeft:'30px', marginRight:'30px'}}>0</span>
            <button style={buttonStyle}>증가</button>
        </div>
    )
}

export default Count
