import React from 'react'
import axios from 'axios';
import { useEffect, useState } from 'react';
import { Button, Table } from 'react-bootstrap';
import Book from './Book';

const BookSearch = () => {
    const [books, setBooks] = useState([]);
    const [loading, setLoading] = useState(false);
    const [page, setPage] = useState(1);
    const [last, setLast] = useState(1);
    const getBooks = async () => {
        const url = `https://dapi.kakao.com/v3/search/book?target=title&query=자바&size=5&page=${page}`;
        const config = {
            headers: {
                "Authorization": "KakaoAK d27ccec046d9726791715798429e0d53"
            }
        }
        setLoading(true);
        const res = await axios.get(url, config);
        // console.log(res);
        setLast(Math.ceil(res.data.meta.pageable_count/5));
        setBooks(res.data.documents);
        setLoading(false);
    }

    useEffect(() => {
        getBooks();
    }, [page]);

    if (loading) return <h1 className='text-center my-5'>로딩중...</h1>

    return (
        <div className='my-5'>
            <style type="text/css">
                {`
                .btn {
                    background-color: blueviolet;
                    color: white;
                    border: none;
                }
                .btn:hover{
                    background-color: purple;
                    color: white;
                }
                .btn:disabled{
                    background-color: blueviolet;
                }
                .btn:active{
                    background-color: pruple;
                }
                `}
            </style>
            <h1 className='text-center mb-5'>도서검색</h1>
            <Table>
                <thead>
                    <tr>
                        <td>이미지</td>
                        <td>제목</td>
                        <td>가격</td>
                        <td>저자</td>
                    </tr>
                </thead>
                <tbody>
                    {books.map(book=>
                        <Book book={book}/>
                    )}
                </tbody>
            </Table>
            <div className='text-center'>
                <Button onClick={()=>setPage(page-1)} disabled={page===1}>이전</Button>
                <span className='mx-3'>{page} / {last}</span>
                <Button onClick={()=>setPage(page+1)} disabled={page===last}>다음</Button>
            </div>
        </div>
    )
}

export default BookSearch
