import React, { useState } from 'react'
import {Button, Table} from 'react-bootstrap';

const Product = () => {
    const [products, setProducts] = useState([
        {"id":1, "name":"냉장고", "price":100},
        {"id":2, "name":"세탁기", "price":80},
        {"id":3, "name":"건조기", "price":80}
    ]);
    const [form, setForm] = useState({
        id:4,
        name:'',
        price:0
    });
    const {id, name, price} = form;
    const onInsert = (e) => {
        e.preventDefault();
        setProducts(products.concat(form));
        alert("저장")
        setForm({
            id:id+1,
            name:'',
            price:0
        })
    }
    const onChange = (e) => {
        setForm({
            ...form,
            [e.target.name]:e.target.value
        });
    }
    return (
        <div>
            <h1>상품관리</h1>
            <div>
                <form onSubmit={onInsert}>
                    <h3>아이디 : {id}</h3>
                    <input placeholder='상품명' value={name} onChange={onChange} name='name'/>&nbsp;&nbsp;
                    <input placeholder='가격' value={price} onChange={onChange} name='price'/>&nbsp;&nbsp;
                    <Button variant='outline-primary'>등록</Button>
                </form>
            </div>
            <hr/>
            <Table bordered>
                {products.map(p=>
                    <tr key={p.id}>
                        <td>{p.id}</td>
                        <td>{p.name}</td>
                        <td>{p.price}</td>
                    </tr>
                )}
            </Table>
        </div>
    )
}

export default Product
