import React, { useEffect, useState } from 'react'

const Posts = () => {
    const [posts, setPosts] = useState([]);

    const getPosts = () => {
        fetch('https://jsonplaceholder.typicode.com/posts')
            .then(response => response.json())
            .then(json => {
                console.log(json);
                setPosts(json);
            })
    }
    useEffect(()=>{
        getPosts();
    }, []);

    return (
        <div>
            <h1>Posts</h1>
        </div>
    )
}

export default Posts
