import React, { useState } from 'react'
import { Button, Table, Form, InputGroup } from 'react-bootstrap';

const Product = () => {
    const [products, setProducts] = useState([
        {"id":1, "name":"냉장고", "price":100},
        {"id":2, "name":"세탁기", "price":80},
        {"id":3, "name":"건조기", "price":80}
    ]);
    const [form, setForm] = useState({
        id:4,
        name:'',
        price:0
    });
    const {id, name, price} = form;
    const onInsert = (e) => {
        e.preventDefault();
        setProducts(products.concat(form));
        alert("저장")
        setForm({
            id:id+1,
            name:'',
            price:0
        })
    }
    const onChange = (e) => {
        setForm({
            ...form,
            [e.target.name]:e.target.value
        });
    }
    return (
        <div className='p-5'>
            <h1>상품관리</h1>
            <div>
                <form onSubmit={onInsert}>
                    <h3>아이디 : {id}</h3>
                    <InputGroup>
                        <Form.Control placeholder='상품명' value={name} onChange={onChange} name='name'/>
                        <Form.Control placeholder='가격' value={price} onChange={onChange} name='price'/>
                        <Button variant='outline-primary'>등록</Button>
                    </InputGroup>
                </form>
            </div>
            <hr/>
            <Table bordered hover>
                <thead>
                    <tr>
                        <th>아이디</th>
                        <th>상품명</th>
                        <th>상품가격</th>
                    </tr>
                </thead>
                <tbody>
                {products.map(p=>
                    <tr key={p.id}>
                        <td className='py-2'>{p.id}</td>
                        <td>{p.name}</td>
                        <td>{p.price}</td>
                    </tr>
                )}
                </tbody>
            </Table>
        </div>
    )
}

export default Product
