import React, { useEffect, useState } from 'react'
import axios from 'axios'
import {Col, Row, Spinner} from 'react-bootstrap'

const MyPage = () => {
  const [loading, setLoading] = useState(false);
  const [user, setUser] = useState({
    uid:'',
    upas:'',
    uname:'',
    photo:'',
    phone:'',
    address1:'',
    address2:'',
    fmtdate:''
  });
  const getUser = () => {
    setLoading(true);
    const res = axios.get(`/users/read/${sessionStorage.getItem("uid")}`);
    // console.log(res);
    setUser(res.data);
    setLoading(false);
  }
  useEffect(()=>{
    getUser();
  }, [])
  if(loading) return <div className='my-5 text-center'><Spinner/></div>
  return (
    <div className='my-5'>
      <h1 className='text-center mb-5'>My Page</h1>
      <Row>
        <Col>
          <div>{photo || <img src="http://via.placeholder.com/200x200" width="200"/>}</div>
          <div>이름 : {uname}</div>
          <div>전화 : {phone}</div>
          <div>주소 : {address1} {address2}</div>
          <div>가입일 : {fmtdate}</div>
        </Col>
      </Row>
    </div>
  )
}

export default MyPage
